package com.syntax.class01;

public class PracticeVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte variable = 29;
		short var1 = 678;
		int var2 = 400000;
		long var3 = 99999999999l;

		float myFloat = 9.99f;
		double meDouble = 199.9; // we dont need letter in the end of the number

		char myChar = 'A';
		char myChar1 = '#';

		boolean myBoo = true;
		boolean myBoo3 = false;

		System.out.println(variable);
		System.out.println(var1);
		System.out.println(var2);
		System.out.println(var3);

		System.out.println(myFloat);
		System.out.println(meDouble);

		System.out.println(myChar);
		System.out.println(myChar1);

		System.out.println(myBoo);
		System.out.print(myBoo3);

	}

}
