package com.syntax.class02;

public class MultipalCharacters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char singleCharacte = 'c';

		String name = "Dinara";

		String lastName = "Smith";

		int number = 123;

		String anything = "123";

		String anything1 = "Hello my friends!!!";

		String oneLetter = "c";
		String oneLetterAndSpce = "C ";

		boolean b = true;
		String str = "true";

		int phone = 1234567890;
		String phoneNumber = "123-456-7890";

		String sentence = "Hello World";
		System.out.println("Hello World");

		int age = 22; // white space
		System.out.println(age);

		// My age is 22
		System.out.println("My age is " + age);

		// My name is Dinara
		System.out.println("My name is " + name);

		String country = "Spain";
		String capital = "Madrid";
		// The capital of Spain is Madrid

		System.out.println("The capital of " + country + " is " + capital);

		String item = "shirt";
		double $price = 19.99;
		char sign = '$';

		// The shirt price is 19.99 // if we want to attach a dollar sing we can declare
		// a var or add it string

		System.out.println("The " + item + " price is " + sign + $price);

		String number2 = "100";
		String number3 = "200";

		System.out.println(number2 + number3); // Output = 100200

		int day = 11;
		String month = "July";

		System.out.println(day + " " + month);

		// 11|July|2021

		int bb = 11;
		String d = "July";

		int year = 2021;

		System.out.println(bb + d + year);

	}

}
